sys_variable_value.value:
(function execute(inputs, outputs) {
    // Get the CSM case record
    var caseGR = new GlideRecord('sn_customerservice_case');
    if (!caseGR.get(inputs.CSMCase)) {
        throw new Error('Case record not found');
    }

    // Get the attachment
    var attachment = new GlideSysAttachment();
    var attachmentGR = attachment.getAttachments('sn_customerservice_case', caseGR.sys_id);

    if (!attachmentGR.next()) {
        throw new Error('No attachment found for the case');
    }

    // Get the base64 encoded content directly
    var base64EncodedImage = attachment.getContentBase64(attachmentGR);

    // Set the output
    outputs.base64_image = base64EncodedImage;

})(inputs, outputs);

