sys_variable_value.value:
(function execute(inputs, outputs) {
    var payload = {
    "model": "gpt-4o",
    "messages": [
        {
        "role": "user",
        "content": [
            {
            "type": "text",
            "text": inputs.Prompt
            },
            {
            "type": "image_url",
            "image_url": {
                "url": "data:image/jpeg;base64," + inputs.Base64Image
            }
            }
        ]
        }
    ],
    "max_tokens": 300
    }

    outputs.payload = payload;
})(inputs, outputs);

