sys_variable_value.value:
(function execute(inputs, outputs) {
    // Parse the payload JSON if it's a string, otherwise use it directly
    var payload = typeof inputs.payload === 'string' ? global.JSON.parse(inputs.payload) : inputs.payload;
    
    // Extract the content from the payload
    var content = payload.choices[0].message.content;
    
    // Set the parsed content as the output
    outputs.content = content;
    
})(inputs, outputs);

